import React from "react";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Moment from "react-moment";

import "./vendor/normalize.css";
import "./vendor/fonts/fonts.css";
import "./App.css";

import flightsMock from "./const/flights.json";

function App() {
  React.useEffect(() => {
    document.title = "Tickets Database";
  }, []);

  const [flights, setFlights] = React.useState([]);

  const [companies, setCompanies] = React.useState([]);

  React.useEffect(() => {
    setFlights(flightsMock.result.flights);
  }, []);

  const [counter, setCounter] = React.useState(2);
  const maxLength = flights.length;
  const plusCounter = () => {
    const newCounter = Math.min(maxLength, counter + 2);
    setCounter(newCounter);
  };

  const [radioValues, setRadioValies] = React.useState("");

  const handleChange = (event) => {
    setRadioValies(event.target.value);
  };

  React.useEffect(() => {
    const aviaCompanyList = [];
    flights.forEach((allFlights) => {
      if (
        !aviaCompanyList.find(
          (item) => item === allFlights.flight.carrier.caption
        )
      ) {
        aviaCompanyList.push(allFlights.flight.carrier.caption);
      }
    });
    setCompanies(aviaCompanyList);
  }, [flights]);


  return (
    <div className="App">
      <>
        {flights.map((allFlights, allFlightsIndex) => {
          allFlights.flight.legs.map((legs, legsIndex) => {
            legs.segments.map((segments, segmentsIndex) => {
              console.log();
            });
          });
        })}
      </>
      <header className="App-header"></header>
      <main className="main-content">
        <form className="search-panel">
          <div className="search-panel__sort">
            <span className="search-panel__title title">Сортировать</span>
            <div>
              <input
                type="radio"
                name="radio"
                id="low-price"
                onChange={handleChange}
                value={"low-price"}
              />
              <label for="low-price"> - по возрастанию цены</label>
            </div>
            <div>
              <input
                type="radio"
                name="radio"
                id="hight-price"
                onChange={handleChange}
                value={"low-price"}
              />
              <label for="hight-price"> - по убыванию цены</label>
            </div>
            <div>
              <input
                type="radio"
                name="radio"
                id="by-time"
                onChange={handleChange}
                value={"low-price"}
              />
              <label for="by-time"> - по времени в пути</label>
            </div>
          </div>
          <div className="search-panel__filter">
            <span className="search-panel__title title">Фильтровать</span>
            <div>
              <input
                type="checkbox"
                id="transfer"
                name="transfer"
                value="transfer"
              ></input>
              <label for="transfer"> - с пересадкой</label>
            </div>
            <div>
              <input
                type="checkbox"
                id="no-transfer"
                name="no-transfer"
                value="no-transfer"
              ></input>
              <label for="no-transfer"> - без пересадок</label>
            </div>
          </div>
          <div className="search-panel__price">
            <span className="search-panel__title title">Цена</span>
            <div>
              <label for="huey">От</label>
              <input
                type="number"
                id="price-form"
                name="price-form"
                value="price-form"
              ></input>
            </div>
            <div>
              <label for="dewey">До</label>
              <input
                type="number"
                id="price-to"
                name="price-to"
                value="price-to"
              ></input>
            </div>
          </div>
          <div className="search-panel__company">
            <span className="search-panel__title title">Авиакомпании</span>
            <div>
              {aviaCompanyList.length !== 0 &&
                aviaCompanyList.map((item, index) => {
                  return (
                    <>
                    <input
                      type="checkbox"
                      id="huey"
                      name="drone"
                      value="huey"

                    ></input>
                    <label for="huey">{item}</label>
                    </>
                  );
                })}
            </div>
          </div>
        </form>
        <section className="search-result">
          {flights &&
            flights.slice(0, counter).map((allFlights, allFlightsIndex) => {
              return (
                <div className="result">
                  <div className="result__label">
                    <h3 className="result__brandname">
                      {allFlights.flight.carrier.caption}
                    </h3>
                    <div className="result__price-section">
                      <h4 className="result__price">
                        {allFlights.flight.price.total.amount}
                        <span>{allFlights.flight.price.total.currency}</span>
                      </h4>
                      <p className="result__price-caption">
                        Стоимость для одного взрослого пассажира
                      </p>
                    </div>
                  </div>
                  {allFlights &&
                    allFlights.flight.legs.map((legs, legsIndex) => {
                      return (
                        <>
                          {legs &&
                            legs.segments.map((segments, segmentsIndex) => {
                              const travelDurationHours = (
                                segments.travelDuration / 60
                              ).toFixed(0);
                              const travelDurationMinutes =
                                segments.travelDuration % 60;
                              const showTravelDurationHours =
                                travelDurationHours === 0
                                  ? null
                                  : travelDurationHours + " " + "ч";
                              const showTravelDurationMinutes =
                                travelDurationMinutes === 0
                                  ? null
                                  : travelDurationMinutes + " " + "мин";

                              const transfer = segments.starting ? (
                                <div className="result__transfer">
                                  <p className="result__transfer-count">
                                    1 пересадка
                                  </p>
                                </div>
                              ) : null;

                              const devide =
                                allFlights.flight.legs.length - 1 ===
                                  legsIndex &&
                                legs.segments.length - 1 ===
                                  segmentsIndex ? null : (
                                  <div className="result__digits-block-divide"></div>
                                );
                              return (
                                <>
                                  <div className="result__places">
                                    <div className="result__places-content">
                                      <p className="result__airport">
                                        {segments.departureCity.caption},&#160;
                                        {segments.departureAirport.caption}
                                        &#160;
                                        <span className="result__reduction">
                                          ({segments.departureAirport.uid})
                                        </span>
                                      </p>
                                      <p className="result__arrow">&#10145;</p>
                                      <p className="result__airport">
                                        {segments.arrivalCity.caption},&#160;
                                        {segments.arrivalAirport.caption}&#160;
                                        <span className="result__reduction">
                                          ({segments.arrivalAirport.uid})
                                        </span>
                                      </p>
                                    </div>
                                  </div>
                                  <div className="result__time-section">
                                    <div className="result__digits-block">
                                      <div className="result__timing result__timing_start">
                                        <p className="result__timing-time">
                                          <Moment
                                            date={segments.departureDate}
                                            locale="en"
                                            format="H:mm"
                                          />
                                        </p>
                                        <p className="result__timing-date">
                                          <Moment
                                            date={segments.departureDate}
                                            locale="en"
                                            format="DD MMM ddd"
                                          />
                                        </p>
                                      </div>
                                      <div className="result__timing-total">
                                        <p className="result__timing-total-value">
                                          <span className="result__timing-total-timer">
                                            &#128337;
                                          </span>
                                          {showTravelDurationHours}&#160;
                                          {showTravelDurationMinutes}
                                        </p>
                                      </div>
                                      <div className="result__timing result__timing_end">
                                        <p className="result__timing-time">
                                          <Moment
                                            date={segments.arrivalDate}
                                            locale="en"
                                            format="H:mm"
                                          />
                                        </p>
                                        <p className="result__timing-date">
                                          <Moment
                                            date={segments.arrivalDate}
                                            locale="en"
                                            format="DD MMM ddd"
                                          />
                                        </p>
                                      </div>
                                    </div>
                                    {transfer}
                                    <p className="result__doer">
                                      Рейс выполняет: {segments.airline.caption}
                                    </p>
                                    {devide}
                                  </div>
                                </>
                              );
                            })}
                        </>
                      );
                    })}
                  <button className="result__select-button">Выбрать</button>
                </div>
              );
            })}
          {maxLength <= counter ? null : (
            <button className="result__loadmore-button" onClick={plusCounter}>
              Показать ещё
            </button>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
